import React, { useReducer, useState, useRef } from "react";
import * as mobilenet from "@tensorflow-models/mobilenet";
import "./App.css";

const stateMachine = {
  initial: "initial",
  states: {
    initial: { on: { next: "loadingModel", text: "Load Model" } },
    loadingModel: { on: { next: "modelReady", text: "Loading Model" } },
    modelReady: { on: { next: "imageReady", text: "Upload Image" } },
    imageReady: {
      on: { next: "identifying", text: "Identifying Image", showImage: true }
    },
    identifying: { on: { next: "complete", text: "Identifying..." } },
    complete: {
      on: {
        next: "modelReady",
        text: "Load Reset",
        showImage: true,
        showResult: true
      }
    }
  }
};

const reducer = (currentState, event) =>
  stateMachine.states[currentState].on[event] || stateMachine.initial;

const App = () => {
  const [appState, dispatch] = useReducer(reducer, stateMachine.initial);
  const [model, setModel] = useState(null)
  const [imageUrl, setImageUrl] = useState(null);
  const inputRef = useRef();
  const next = () => dispatch("next");

  const load = async () => {
    next()
    const mobilenetModel = await mobilenet.load();
    setModel(mobilenetModel)
    next()
  }

  const reset = () => {
    setResults([])
    setImageUrl(null)
    next();
   }

  const buttonProps = {
    initial: { text: "Load Model", action: load },
    loadingModel: { text: "Loading Model…", action: () => {} },
    modelReady: { text: "Upload Image", action: () => {} },
    imageReady: { text: "Identify Image", action: () => {} },
    identifying: { text: "Identifying…", action: () => {} },
    complete: { text: "Reset", action: reset }
  };

  const handleUpload = event => {
    const { files } = event.target;
    if (files.length > 0) {
    const url = URL.createObjectURL(files[0])
    setImageUrl(url)
    next();
    }
   }


  return (
    <div>
      { showImage &&
 <img
 src={imageUrl}
 alt="upload-preview"
 ref={imageRef}
 />
}
      <input type="file" accept="image/*" capture="camera" ref={inputRef} onChange={handleUpload}></input>
      <button onClick={buttonProps[appState].action}>
        {buttonProps[appState].text}
      </button>
    </div>
  );
};

export default App;
